import { Stack } from "expo-router";
import { useEffect } from "react";
import * as Notifications from 'expo-notifications';
import { initDatabase } from "./utils/database";
import { registerForPushNotificationsAsync, scheduleDailyNotifications } from "./utils/notifications";

Notifications.setNotificationHandler({
  handleNotification: async () => ({
    shouldShowAlert: true,
    shouldPlaySound: true,
    shouldSetBadge: false,
  }),
});

export default function RootLayout() {
  useEffect(() => {
    const setup = async () => {
      await initDatabase();
      await registerForPushNotificationsAsync();
      await scheduleDailyNotifications();
    };
    setup();
  }, []);

  return (
    <>
      {/* Root stack controls screen transitions for the whole app */}
      <Stack>

import { useState, useRef, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Alert, ActivityIndicator } from 'react-native';
import { CameraView, useCameraPermissions, useMicrophonePermissions } from 'expo-camera';
import * as FileSystem from 'expo-file-system';
import * as Location from 'expo-location';
import { insertRecord } from '../utils/database';

export default function HomeScreen() {
  const [facing, setFacing] = useState('front');
  const [permission, requestPermission] = useCameraPermissions();
  const [micPermission, requestMicPermission] = useMicrophonePermissions();
  const [sentiment, setSentiment] = useState(3);
  const [isRecording, setIsRecording] = useState(false);
  const cameraRef = useRef(null);

  useEffect(() => {
    (async () => {
      await requestPermission();
      await requestMicPermission();
      await Location.requestForegroundPermissionsAsync();
    })();
  }, []);

  if (!permission || !micPermission) {
    return <View style={styles.container}><ActivityIndicator /></View>;
  }

  if (!permission.granted || !micPermission.granted) {
    return (
      <View style={styles.container}>
        <Text style={styles.message}>We need your permission to show the camera</Text>
        <TouchableOpacity onPress={requestPermission} style={styles.button}>
          <Text style={styles.text}>Grant Permission</Text>
        </TouchableOpacity>
      </View>
    );
  }

  const toggleCameraFacing = () => {
    setFacing(current => (current === 'back' ? 'front' : 'back'));
  };

  const recordVideo = async () => {
    if (cameraRef.current && !isRecording) {
      setIsRecording(true);
      try {
        const video = await cameraRef.current.recordAsync({ maxDuration: 1 });

        // Get Location
        let location = await Location.getCurrentPositionAsync({});

        // Save to permanent storage
        const fileName = video.uri.split('/').pop();
        const newPath = FileSystem.documentDirectory + fileName;
        await FileSystem.moveAsync({
          from: video.uri,
          to: newPath,
        });

        // Save to DB
        await insertRecord(
          newPath,
          sentiment,
          location.coords.latitude,
          location.coords.longitude
        );

        Alert.alert("Success", "Vlog recorded!");
      } catch (error) {
        console.error(error);
        Alert.alert("Error", "Failed to record vlog.");
      } finally {
        setIsRecording(false);
      }
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.cameraContainer}>
        <CameraView 
          style={styles.camera} 
          facing={facing} 
          mode="video"
          ref={cameraRef}
        >
          <View style={styles.buttonContainer}>
            <TouchableOpacity style={styles.flipButton} onPress={toggleCameraFacing}>
              <Text style={styles.text}>Flip</Text>
            </TouchableOpacity>
          </View>
        </CameraView>
      </View>

      <View style={styles.controlsContainer}>
        <Text style={styles.label}>How are you feeling? (1-5)</Text>
        <View style={styles.sentimentContainer}>
          {[1, 2, 3, 4, 5].map((level) => (
            <TouchableOpacity
              key={level}
              style={[
                styles.sentimentButton,
                sentiment === level && styles.sentimentButtonSelected
              ]}
              onPress={() => setSentiment(level)}
            >
              <Text style={[
                styles.sentimentText,
                sentiment === level && styles.sentimentTextSelected
              ]}>{level}</Text>
            </TouchableOpacity>
          ))}
        </View>

        <TouchableOpacity
          style={[styles.recordButton, isRecording && styles.recordButtonDisabled]}
          onPress={recordVideo}
          disabled={isRecording}
        >
          <Text style={styles.recordButtonText}>
            {isRecording ? "Recording..." : "Record 1s Vlog"}
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f0f0f0',
  },
  message: {
    textAlign: 'center',
    paddingBottom: 10,
  },
  cameraContainer: {
    flex: 1,
    margin: 10,
    borderRadius: 20,
    overflow: 'hidden',
  },
  camera: {
    flex: 1,
  },
  buttonContainer: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: 'transparent',
    margin: 20,
  },
  flipButton: {
    alignSelf: 'flex-end',
    alignItems: 'center',
    backgroundColor: 'rgba(0,0,0,0.5)',
    padding: 10,
    borderRadius: 5,
  },
  text: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
  },
  controlsContainer: {
    padding: 20,
    backgroundColor: 'white',
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
    alignItems: 'center',
  },
  label: {
    fontSize: 18,
    marginBottom: 10,
    fontWeight: '600',
  },
  sentimentContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '100%',
    marginBottom: 20,
  },
  sentimentButton: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#e0e0e0',
    justifyContent: 'center',
    alignItems: 'center',
  },
  sentimentButtonSelected: {
    backgroundColor: '#007AFF',
  },
  sentimentText: {
    fontSize: 20,
    color: '#333',
  },
  sentimentTextSelected: {
    color: 'white',
  },
  recordButton: {
    backgroundColor: '#FF3B30',
    paddingVertical: 15,
    paddingHorizontal: 40,
    borderRadius: 30,
    width: '100%',
    alignItems: 'center',
  },
  recordButtonDisabled: {
    backgroundColor: '#ffb3b3',
  },
  recordButtonText: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

import { Tabs } from "expo-router";

export default function TabsLayout() {
  return (
    <Tabs>
      {/* First tab uses the index.js screen in this folder */}
      <Tabs.Screen
        name="index"
        options={{
          title: "Record",
        }}
      />
      <Tabs.Screen
        name="history"
        options={{
          title: "History",
        }}
      />
      <Tabs.Screen
        name="settings"
        options={{
          title: "Settings",
        }}
      />
    </Tabs>
  );
}

