{"content":"import * as SQLite from 'expo-sqlite';

let db;

export const initDatabase = async () => {
  try {
    db = await SQLite.openDatabaseAsync('emogo.db');
    await db.execAsync(`
      CREATE TABLE IF NOT EXISTS records (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        videoUri TEXT NOT NULL,
        sentiment INTEGER NOT NULL,
        latitude REAL,
        longitude REAL,
        timestamp TEXT NOT NULL
      );
    `);
    console.log('Database initialized');
  } catch (error) {
    console.error('Error initializing database:', error);
  }
};

export const insertRecord = async (videoUri, sentiment, latitude, longitude) => {
  if (!db) await initDatabase();
  try {
    const timestamp = new Date().toISOString();
    const result = await db.runAsync(
      'INSERT INTO records (videoUri, sentiment, latitude, longitude, timestamp) VALUES (?, ?, ?, ?, ?)',
      [videoUri, sentiment, latitude, longitude, timestamp]
    );
    return result.lastInsertRowId;
  } catch (error) {
    console.error('Error inserting record:', error);
    throw error;
  }
};

export const fetchRecords = async () => {
  if (!db) await initDatabase();
  try {
    const allRows = await db.getAllAsync('SELECT * FROM records ORDER BY timestamp DESC');
    return allRows;
  } catch (error) {
    console.error('Error fetching records:', error);
    return [];
  }
};
