{"content":"import * as Notifications from 'expo-notifications';
import { Platform } from 'react-native';

export async function registerForPushNotificationsAsync() {
  let token;

  const { status: existingStatus } = await Notifications.getPermissionsAsync();
  let finalStatus = existingStatus;
  if (existingStatus !== 'granted') {
    const { status } = await Notifications.requestPermissionsAsync();
    finalStatus = status;
  }
  if (finalStatus !== 'granted') {
    alert('Failed to get push token for push notification!');
    return;
  }

  if (Platform.OS === 'android') {
    Notifications.setNotificationChannelAsync('default', {
      name: 'default',
      importance: Notifications.AndroidImportance.MAX,
      vibrationPattern: [0, 250, 250, 250],
      lightColor: '#FF231F7C',
    });
  }
}

export async function scheduleDailyNotifications() {
  // Cancel all existing notifications to avoid duplicates
  await Notifications.cancelAllScheduledNotificationsAsync();

  const schedules = [
    { hour: 10, minute: 0, title: "早安！", body: "記得記錄一下現在的心情喔！" },
    { hour: 15, minute: 0, title: "午安！", body: "下午茶時間，來拍個一秒影片吧！" },
    { hour: 20, minute: 0, title: "晚安！", body: "今天過得如何？記錄下來吧！" },
  ];

  for (const schedule of schedules) {
    await Notifications.scheduleNotificationAsync({
      content: {
        title: schedule.title,
        body: schedule.body,
      },
      trigger: {
        hour: schedule.hour,
        minute: schedule.minute,
        repeats: true,
      },
    });
  }
}
