{"content":"import { useState, useCallback } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { useFocusEffect } from 'expo-router';
import { Video, ResizeMode } from 'expo-av';
import * as FileSystem from 'expo-file-system';
import * as Sharing from 'expo-sharing';
import { fetchRecords } from '../utils/database';

export default function HistoryScreen() {
  const [records, setRecords] = useState([]);

  useFocusEffect(
    useCallback(() => {
      loadRecords();
    }, [])
  );

  const loadRecords = async () => {
    const data = await fetchRecords();
    setRecords(data);
  };

  const exportData = async () => {
    try {
      const fileUri = FileSystem.cacheDirectory + 'emogo_records.json';
      await FileSystem.writeAsStringAsync(fileUri, JSON.stringify(records, null, 2));

      if (!(await Sharing.isAvailableAsync())) {
        Alert.alert("Error", "Sharing is not available on this device");
        return;
      }

      await Sharing.shareAsync(fileUri);
    } catch (error) {
      console.error(error);
      Alert.alert("Error", "Failed to export data");
    }
  };

  const renderItem = ({ item }) => (
    <View style={styles.card}>
      <View style={styles.header}>
        <Text style={styles.date}>{new Date(item.timestamp).toLocaleString()}</Text>
        <View style={styles.sentimentBadge}>
          <Text style={styles.sentimentText}>Mood: {item.sentiment}</Text>
        </View>
      </View>

      <Text style={styles.location}>
        Loc: {item.latitude?.toFixed(4)}, {item.longitude?.toFixed(4)}
      </Text>

      <Video
        style={styles.video}
        source={{ uri: item.videoUri }}
        useNativeControls
        resizeMode={ResizeMode.CONTAIN}
        isLooping
      />
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.topBar}>
        <Text style={styles.title}>History</Text>
        <TouchableOpacity style={styles.exportButton} onPress={exportData}>
          <Text style={styles.exportButtonText}>Export</Text>
        </TouchableOpacity>
      </View>

      <FlatList
        data={records}
        keyExtractor={(item) => item.id.toString()}
        renderItem={renderItem}
        contentContainerStyle={styles.list}
        ListEmptyComponent={<Text style={styles.emptyText}>No records yet.</Text>}
      />
    </View>
  );
}

const styles = StyleSheet.create({
