import * as Notifications from 'expo-notifications';
import { Platform } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

export const NOTIFICATION_SETTINGS_KEY = 'notification_settings';

export const DEFAULT_NOTIFICATIONS = {
  morning: { hour: 10, minute: 0, enabled: true },
  afternoon: { hour: 15, minute: 0, enabled: true },
  evening: { hour: 20, minute: 0, enabled: true },
};

export async function registerForPushNotificationsAsync() {
  let token;

  const { status: existingStatus } = await Notifications.getPermissionsAsync();
  let finalStatus = existingStatus;
  if (existingStatus !== 'granted') {
    const { status } = await Notifications.requestPermissionsAsync();
    finalStatus = status;
  }
  if (finalStatus !== 'granted') {
    // alert('Failed to get push token for push notification!');
    console.log('Failed to get push token for push notification!');
    return;
  }

  if (Platform.OS === 'android') {
    Notifications.setNotificationChannelAsync('default', {
      name: 'default',
      importance: Notifications.AndroidImportance.MAX,
      vibrationPattern: [0, 250, 250, 250],
      lightColor: '#FF231F7C',
    });
  }
}

export async function getNotificationSettings() {
  try {
    const jsonValue = await AsyncStorage.getItem(NOTIFICATION_SETTINGS_KEY);
    return jsonValue != null ? JSON.parse(jsonValue) : DEFAULT_NOTIFICATIONS;
  } catch (e) {
    console.error("Error reading notification settings", e);
    return DEFAULT_NOTIFICATIONS;
  }
}

export async function saveNotificationSettings(settings) {
  try {
    await AsyncStorage.setItem(NOTIFICATION_SETTINGS_KEY, JSON.stringify(settings));
    await scheduleDailyNotifications(); // Reschedule immediately
  } catch (e) {
    console.error("Error saving notification settings", e);
  }
}

export async function scheduleDailyNotifications() {
  // Cancel all existing notifications to avoid duplicates
  await Notifications.cancelAllScheduledNotificationsAsync();

  const settings = await getNotificationSettings();

  const schedules = [
    { ...settings.morning, title: "早安！", body: "記得記錄一下現在的心情喔！" },
    { ...settings.afternoon, title: "午安！", body: "下午茶時間，來拍個一秒影片吧！" },
    { ...settings.evening, title: "晚安！", body: "今天過得如何？記錄下來吧！" },
  ];

  for (const schedule of schedules) {
    if (schedule.enabled) {
      await Notifications.scheduleNotificationAsync({
        content: {
          title: schedule.title,
          body: schedule.body,
        },
        trigger: {
          hour: schedule.hour,
          minute: schedule.minute,
          repeats: true,
        },
      });
    }
  }
}

import { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Switch, Platform, TouchableOpacity, ScrollView } from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import { getNotificationSettings, saveNotificationSettings, DEFAULT_NOTIFICATIONS } from '../utils/notifications';

export default function SettingsScreen() {
  const [settings, setSettings] = useState(DEFAULT_NOTIFICATIONS);
  const [showPicker, setShowPicker] = useState(null); // 'morning', 'afternoon', 'evening' or null

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    const loadedSettings = await getNotificationSettings();
    setSettings(loadedSettings);
  };

  const updateTime = (key, event, selectedDate) => {
    if (Platform.OS === 'android') {
      setShowPicker(null);
    }

    if (selectedDate) {
      const newSettings = {
        ...settings,
        [key]: {
          ...settings[key],
          hour: selectedDate.getHours(),
          minute: selectedDate.getMinutes(),
        },
      };
      setSettings(newSettings);
      saveNotificationSettings(newSettings);
    }
  };

  const toggleSwitch = (key) => {
    const newSettings = {
      ...settings,
      [key]: {
        ...settings[key],
        enabled: !settings[key].enabled,
      },
    };
    setSettings(newSettings);
    saveNotificationSettings(newSettings);
  };

  const formatTime = (hour, minute) => {
    const date = new Date();
    date.setHours(hour);
    date.setMinutes(minute);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const renderSettingItem = (key, label) => {
    const setting = settings[key];
    const date = new Date();
    date.setHours(setting.hour);
    date.setMinutes(setting.minute);

    return (
      <View style={styles.settingItem}>
        <View style={styles.settingHeader}>
          <Text style={styles.settingLabel}>{label}</Text>
          <Switch
            trackColor={{ false: "#767577", true: "#81b0ff" }}
            thumbColor={setting.enabled ? "#007AFF" : "#f4f3f4"}
            onValueChange={() => toggleSwitch(key)}
            value={setting.enabled}
          />
        </View>

        {setting.enabled && (
          <View style={styles.timeContainer}>
            <Text style={styles.timeText}>Notification Time:</Text>
            {Platform.OS === 'ios' ? (
              <DateTimePicker
                value={date}
                mode="time"
                display="compact"
                onChange={(e, d) => updateTime(key, e, d)}
                style={{ width: 100 }}
              />
            ) : (
              <TouchableOpacity 
                onPress={() => setShowPicker(key)}
                style={styles.timeButton}
              >
                <Text style={styles.timeButtonText}>{formatTime(setting.hour, setting.minute)}</Text>
              </TouchableOpacity>
            )}
          </View>
        )}

        {Platform.OS === 'android' && showPicker === key && (
          <DateTimePicker
            value={date}
            mode="time"
            display="default"
            onChange={(e, d) => updateTime(key, e, d)}
          />
        )}
      </View>
    );
  };

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Notification Settings</Text>

      {renderSettingItem('morning', 'Morning Reminder')}
      {renderSettingItem('afternoon', 'Afternoon Reminder')}
      {renderSettingItem('evening', 'Evening Reminder')}

    </ScrollView>
  );
}

