const SENTIMENT_LABELS = {
  1: "Very Bad",
  2: "Bad",
  3: "Neutral",
  4: "Good",
  5: "Very Good"
};

export default function HomeScreen() {
  const [facing, setFacing] = useState('front');
  const [permission, requestPermission] = useCameraPermissions();
  const [micPermission, requestMicPermission] = useMicrophonePermissions();
  const [sentiment, setSentiment] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [recordedVideoUri, setRecordedVideoUri] = useState(null);
  const cameraRef = useRef(null);

  useEffect(() => {
    (async () => {
      await requestPermission();
      await requestMicPermission();
      await Location.requestForegroundPermissionsAsync();
    })();
  }, []);

  if (!permission || !micPermission) {
    return <View style={styles.container}><ActivityIndicator /></View>;
  }

  if (!permission.granted || !micPermission.granted) {
    return (
      <View style={styles.container}>
        <Text style={styles.message}>We need your permission to show the camera</Text>
        <TouchableOpacity onPress={requestPermission} style={styles.button}>
          <Text style={styles.text}>Grant Permission</Text>
        </TouchableOpacity>
      </View>
    );
  }

  const toggleCameraFacing = () => {
    setFacing(current => (current === 'back' ? 'front' : 'back'));
  };

  const recordVideo = async () => {
    if (cameraRef.current && !isRecording) {
      setIsRecording(true);
      try {
        const video = await cameraRef.current.recordAsync({ maxDuration: 1 });
        setRecordedVideoUri(video.uri);
      } catch (error) {
        console.error(error);
        Alert.alert("Error", "Failed to record vlog.");
      } finally {
        setIsRecording(false);
      }
    }
  };

  const retakeVideo = () => {
    setRecordedVideoUri(null);
    setSentiment(null);
  };

  const saveRecord = async () => {
    if (!recordedVideoUri || !sentiment) return;

    try {
      // Get Location
      let location = await Location.getCurrentPositionAsync({});

      // Save to permanent storage
      const fileName = recordedVideoUri.split('/').pop();
      const newPath = FileSystem.documentDirectory + fileName;
      await FileSystem.moveAsync({
        from: recordedVideoUri,
        to: newPath,
      });

      // Save to DB
      await insertRecord(
        newPath,
        sentiment,
        location.coords.latitude,
        location.coords.longitude
      );

      Alert.alert("Success", "Record saved!");
      setRecordedVideoUri(null);
      setSentiment(null);
    } catch (error) {
      console.error(error);
      Alert.alert("Error", "Failed to save record.");
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.cameraContainer}>
        {recordedVideoUri ? (
          <Video
            style={styles.camera}
            source={{ uri: recordedVideoUri }}
