const saveRecord = async () => {
  if (!recordedVideoUri || !sentiment) return;

  try {
    // Get Location (Optional)
    let latitude = null;
    let longitude = null;
    try {
      const location = await Location.getCurrentPositionAsync({
        accuracy: Location.Accuracy.Balanced,
      });
      latitude = location.coords.latitude;
      longitude = location.coords.longitude;
    } catch (locError) {
      console.log("Location retrieval failed, saving without location:", locError);
    }

    // Save to permanent storage
    const fileName = recordedVideoUri.split('/').pop();
    const newPath = FileSystem.documentDirectory + fileName;

    // Check if file exists at source
    const fileInfo = await FileSystem.getInfoAsync(recordedVideoUri);
    if (!fileInfo.exists) {
      throw new Error("Source video file not found");
    }

    await FileSystem.moveAsync({
      from: recordedVideoUri,
      to: newPath,
    });

    // Save to DB
    await insertRecord(
      newPath,
      sentiment,
      latitude,
      longitude
    );

    Alert.alert("Success", "Record saved!");
    setRecordedVideoUri(null);
    setSentiment(null);
  } catch (error) {
    console.error("Save error:", error);
    Alert.alert("Error", `Failed to save record: ${error.message}`);
  }
};

import * as SQLite from 'expo-sqlite';

let db;

export const initDatabase = async () => {
  if (db) return; // Already initialized
  try {
    db = await SQLite.openDatabaseAsync('emogo.db');
    await db.execAsync(`
      CREATE TABLE IF NOT EXISTS records (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        videoUri TEXT NOT NULL,
        sentiment INTEGER NOT NULL,
        latitude REAL,
        longitude REAL,
        timestamp TEXT NOT NULL
      );
    `);
    console.log('Database initialized');
  } catch (error) {
    console.error('Error initializing database:', error);
    throw error; // Re-throw to handle in caller
  }
};

export const insertRecord = async (videoUri, sentiment, latitude, longitude) => {
  try {
    if (!db) await initDatabase();
    const timestamp = new Date().toISOString();
    const result = await db.runAsync(
      'INSERT INTO records (videoUri, sentiment, latitude, longitude, timestamp) VALUES (?, ?, ?, ?, ?)',
      [videoUri, sentiment, latitude, longitude, timestamp]
    );
    return result.lastInsertRowId;
  } catch (error) {
    console.error('Error inserting record:', error);
    throw error;
  }
};

export const fetchRecords = async () => {
  try {
    if (!db) await initDatabase();
    const allRows = await db.getAllAsync('SELECT * FROM records ORDER BY timestamp DESC');
    return allRows;
  } catch (error) {
    console.error('Error fetching records:', error);
    return [];
  }
};

