import { useState, useRef, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Alert, ActivityIndicator } from 'react-native';
import { CameraView, useCameraPermissions, useMicrophonePermissions } from 'expo-camera';
import { Video, ResizeMode } from 'expo-av';
import * as MediaLibrary from 'expo-media-library';
import * as Location from 'expo-location';
import { insertRecord } from '../utils/database';

const SENTIMENT_LABELS = {
  1: "Very Bad",
  2: "Bad",
  3: "Neutral",
  4: "Good",
  5: "Very Good"
};

export default function HomeScreen() {
  const [facing, setFacing] = useState('front');
  const [permission, requestPermission] = useCameraPermissions();
  const [micPermission, requestMicPermission] = useMicrophonePermissions();
  const [mediaPermission, requestMediaPermission] = MediaLibrary.usePermissions();
  const [sentiment, setSentiment] = useState(null);
  const [isRecording, setIsRecording] = useState(false);
  const [recordedVideoUri, setRecordedVideoUri] = useState(null);
  const cameraRef = useRef(null);

  useEffect(() => {
    (async () => {
      await requestPermission();
      await requestMicPermission();
      await requestMediaPermission();
      await Location.requestForegroundPermissionsAsync();
    })();
  }, []);

  if (!permission || !micPermission || !mediaPermission) {
    return <View style={styles.container}><ActivityIndicator /></View>;
  }

  if (!permission.granted || !micPermission.granted || !mediaPermission.granted) {
    return (
      <View style={styles.container}>
        <Text style={styles.message}>We need your permission to show the camera and access media library</Text>
        <TouchableOpacity onPress={() => {
          requestPermission();
          requestMediaPermission();
        }} style={styles.button}>
          <Text style={styles.text}>Grant Permission</Text>
        </TouchableOpacity>
      </View>
    );
  }

    // Save to MediaLibrary
    const asset = await MediaLibrary.createAssetAsync(recordedVideoUri);

    // Save to DB
    await insertRecord(
      asset.uri,
      sentiment,
      latitude,
      longitude
    );

    Alert.alert("Success", "Record saved!");
    setRecordedVideoUri(null);
    setSentiment(null);
  } catch (error) {
    console.error("Save error:", error);
    Alert.alert("Error", `Failed to save record: ${error.message}`);
  }
};

export async function scheduleDailyNotifications() {
  // Cancel all existing notifications to avoid duplicates
  await Notifications.cancelAllScheduledNotificationsAsync();

  const settings = await getNotificationSettings();

  const schedules = [
    { ...settings.morning, title: "早安！", body: "記得記錄一下現在的心情喔！" },
    { ...settings.afternoon, title: "午安！", body: "下午茶時間，來拍個一秒影片吧！" },
    { ...settings.evening, title: "晚安！", body: "今天過得如何？記錄下來吧！" },
  ];

  for (const schedule of schedules) {
    if (schedule.enabled) {
      await Notifications.scheduleNotificationAsync({
        content: {
          title: schedule.title,
          body: schedule.body,
        },
        trigger: {
          type: Notifications.SchedulableTriggerInputTypes.DAILY,
          hour: schedule.hour,
          minute: schedule.minute,
        },
      });
    }
  }
}

