import { useState, useCallback } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { useFocusEffect } from 'expo-router';
import { Video, ResizeMode } from 'expo-av';
import * as FileSystem from 'expo-file-system';
import { writeAsStringAsync, copyAsync, makeDirectoryAsync, deleteAsync } from 'expo-file-system/legacy';
import * as Sharing from 'expo-sharing';
import * as MediaLibrary from 'expo-media-library';
import { zip } from 'react-native-zip-archive';
import { fetchRecords } from '../utils/database';

export default function HistoryScreen() {
  const [records, setRecords] = useState([]);
  const [isExporting, setIsExporting] = useState(false);

  useFocusEffect(
    useCallback(() => {
      loadRecords();
    }, [])
  );

  const loadRecords = async () => {
    const data = await fetchRecords();
    setRecords(data);
  };

  const exportData = async () => {
    if (isExporting) return;
    setIsExporting(true);
    try {
      const timestamp = new Date().getTime();
      const exportDir = FileSystem.cacheDirectory + `export_${timestamp}/`;
      const zipFile = FileSystem.cacheDirectory + `emogo_export_${timestamp}.zip`;

      // Create temp directory
      await makeDirectoryAsync(exportDir, { intermediates: true });

      const exportRecords = [];

      for (const record of records) {
        let videoFileName = `video_${record.id}.mp4`;
        let videoExportPath = exportDir + videoFileName;
        let videoCopied = false;

        try {
          if (record.assetId) {
            const assetInfo = await MediaLibrary.getAssetInfoAsync(record.assetId);
            if (assetInfo && assetInfo.localUri) {
              await copyAsync({ from: assetInfo.localUri, to: videoExportPath });
              videoCopied = true;
            }
          } else if (record.videoUri && record.videoUri.startsWith('file://')) {
             // Legacy support for file system paths
             await copyAsync({ from: record.videoUri, to: videoExportPath });
             videoCopied = true;
          }
        } catch (e) {
          console.log(`Failed to copy video for record ${record.id}:`, e);
        }

        exportRecords.push({
          ...record,
          videoUri: videoCopied ? videoFileName : null, // Relative path in zip
          originalVideoUri: record.videoUri
        });
      }

      // Write JSON
      await writeAsStringAsync(exportDir + 'records.json', JSON.stringify(exportRecords, null, 2));

      // Zip
      await zip(exportDir, zipFile);

      // Share
      if (!(await Sharing.isAvailableAsync())) {
        Alert.alert("Error", "Sharing is not available on this device");
        return;
      }

      await Sharing.shareAsync(zipFile);

      // Cleanup
      try {
        await deleteAsync(exportDir, { idempotent: true });
        // await deleteAsync(zipFile, { idempotent: true }); // Keep zip for a bit or let OS clean cache
      } catch (e) {
        console.log("Cleanup error", e);
      }

    } catch (error) {
      console.error(error);
      Alert.alert("Error", "Failed to export data: " + error.message);
    } finally {
      setIsExporting(false);
    }
  };

