import { useState, useCallback } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { useFocusEffect } from 'expo-router';
import { Video, ResizeMode } from 'expo-av';
import * as FileSystem from 'expo-file-system';
import { writeAsStringAsync, readAsStringAsync, deleteAsync } from 'expo-file-system/legacy';
import * as Sharing from 'expo-sharing';
import * as MediaLibrary from 'expo-media-library';
import JSZip from 'jszip';
import { fetchRecords } from '../utils/database';

export default function HistoryScreen() {
  const [records, setRecords] = useState([]);
  const [isExporting, setIsExporting] = useState(false);

  useFocusEffect(
    useCallback(() => {
      loadRecords();
    }, [])
  );

  const loadRecords = async () => {
    const data = await fetchRecords();
    setRecords(data);
  };

  const exportData = async () => {
    if (isExporting) return;
    setIsExporting(true);
    try {
      const timestamp = new Date().getTime();
      const zipFile = FileSystem.cacheDirectory + `emogo_export_${timestamp}.zip`;
      const zip = new JSZip();

      const exportRecords = [];

      for (const record of records) {
        let videoFileName = `video_${record.id}.mp4`;
        let videoAdded = false;

        try {
          let sourceUri = null;
          if (record.assetId) {
            const assetInfo = await MediaLibrary.getAssetInfoAsync(record.assetId);
            if (assetInfo && assetInfo.localUri) {
              sourceUri = assetInfo.localUri;
            }
          } else if (record.videoUri && record.videoUri.startsWith('file://')) {
             sourceUri = record.videoUri;
          }

          if (sourceUri) {
            // Read video as base64
            const videoContent = await readAsStringAsync(sourceUri, { encoding: FileSystem.EncodingType.Base64 });
            zip.file(videoFileName, videoContent, { base64: true });
            videoAdded = true;
          }
        } catch (e) {
          console.log(`Failed to add video for record ${record.id}:`, e);
        }

        exportRecords.push({
          ...record,
          videoUri: videoAdded ? videoFileName : null, // Relative path in zip
          originalVideoUri: record.videoUri
        });
      }

      // Add JSON
      zip.file('records.json', JSON.stringify(exportRecords, null, 2));

      // Generate zip
      const zipContent = await zip.generateAsync({ type: 'base64' });
      await writeAsStringAsync(zipFile, zipContent, { encoding: FileSystem.EncodingType.Base64 });

      // Share
      if (!(await Sharing.isAvailableAsync())) {
        Alert.alert("Error", "Sharing is not available on this device");
        return;
      }

      await Sharing.shareAsync(zipFile);

      // Cleanup
      try {
        await deleteAsync(zipFile, { idempotent: true });
